load three_moons.matdataSize = size(three_moons, 2);xbar = mean(mean(three_moons));S = cov(three_moons'); % three_moons is 100 x 1500[V D] = eig(S);%put the eigenvectors with the largest eigenvalues in front in columnsV = fliplr(V);%order eigenvalues from largest to smallestD = fliplr(flipud(D));k = 2; % dimension of projected spacepAxes = V(:, 1:k);projCoords = zeros(k, dataSize); % coordinates in subspacefor j=1:dataSize  for i=1:k    projCoords(i, j) = dot(pAxes(:, i), three_moons(:, j));  endendplot(projCoords(1, :), projCoords(2, :), 'b*')