load iris_data.mat[N, d] = size(data);xbar = mean(data);S = cov(data);[V, D] = eigs(S, 2);projData = zeros(N, 2);for i = 1:N  projData(i, 1) = dot(data(i, :) - xbar, V(:, 1)');  projData(i, 2) = dot(data(i, :) - xbar, V(:, 2)');endclf;hold on;plot(projData(1:50, 1), projData(1:50, 2), 'r.');plot(projData(51:100, 1), projData(51:100, 2), 'go');plot(projData(101:150, 1), projData(101:150, 2), 'b+');legend('D1', 'D2', 'D3');