% phi: R^d -> R^d+1function y = phi(x)  y = [x; 1];endfunction w = findCoefficient(C, t, lambda)  % Each column of C should be an observation  N = size(t);    % Calculate coefficients  % n-th column is phi(x_n)  Phi = phi(C(:, 1));  for i = 2:N    Phi = [Phi, phi(C(:, i))];  end  % Solve least squares (Phi * Phi + lambda * I)^T * w = Phi * t  w = ((Phi * t) \ (Phi * Phi' + lambda * eye(size(C, 1) + 1)))';end